import UserInput from "./Components/UserInput"
import {Bar} from 'react-chartjs-2';



function App() {
  var array = [1, 2, 3, 4];
  var element_1, element_2, element_3, element_4;
  function addStockHandler(stock) {
    array = stock;
    var a = array[0], b = array[1], c = array[2], d = array[3];
    const state = {
      labels: ['January', 'February', 'March',
               'April', 'May'],
      datasets: [
        {
          label: 'Rainfall',
          backgroundColor: 'rgba(75,192,192,1)',
          borderColor: 'rgba(0,0,0,1)',
          borderWidth: 2,
          data: [a, b, c, d]
        }
      ]
    }

  }
  function AddStockHandler() {
    return (<div>
      <Bar
        data={state}
        options={{
          title:{
            display:true,
            text:'Average Rainfall per month',
            fontSize:20
          },
          legend:{
            display:true,
            position:'right'
          }
        }}
      />
    </div>);
  }


   return (
     <div> 
       <UserInput onAddStock = {addStockHandler}></UserInput>

     </div>
   );
}

export default App;
