import { useState } from "react/cjs/react.development";

function UserInput() {
  var name, temperature;
  const [temperatures, setTemperature] = useState();
  function onChangeHandler(event) {
    name = event.target.value;
  }
  function onClickHandler(event) {
    event.preventDefault();
      fetch('http://api.openweathermap.org/data/2.5/weather?q='+name+'&appid=69ac9fe5ae75cc26bea2df54ff57d776&units=metric').then(response => {
          return response.json();
      }).then((data) => {
        temperature = data.main.temp;
        setTemperature(Math.floor(temperature));
      });
  }
  return (
    <div>
      <form>
        <input className = "inputValue" type="text" placeholder="Enter city name" onChange = {onChangeHandler}></input>
        <button onClick = {onClickHandler} >
          Submit!
        </button>
        <h1>Celsius:{temperatures}</h1>
      </form>
    </div>
  );
}

export default UserInput;
