function UserInput() {
  var name;
  function onChangeHandler(event) {
    name = event.target.value;
  }
  function onClickHandler() {
      alert(name);
      return 1;
  }
  return (
    <div>
      <form>
        <input class = "inputValue" type="text" placeholder="Enter city name" onChage = {onChangeHandler}></input>
        <button onClick = {onClickHandler} >
          Submit!
        </button>
      </form>
    </div>
  );
}

export default UserInput;
