function UserInput() {
  var name, temperature, OK = 0;
  function onChangeHandler(event) {
    name = event.target.value;
  }
  function onClickHandler() {
      while(OK == 0) {
      fetch('http://api.openweathermap.org/data/2.5/weather?q=London&appid=52b987646ad8d9b43f889187f3d68809').then(response => {
          return response.json();
      }).then((data) => {
          OK = 1;
        alert(data.main.temp);
      });
    }
      
  }
  return (
    <div>
      <form>
        <input className = "inputValue" type="text" placeholder="Enter city name" onChange = {onChangeHandler}></input>
        <button onClick = {onClickHandler} >
          Submit!
        </button>
      </form>
    </div>
  );
}

export default UserInput;
