import { useState } from "react/cjs/react.development";

function UserInput() {
  var name, price;
  const [prices, setPrice] = useState();
  function onChangeHandler(event) {
    name = event.target.value;
  }
  function onClickHandler(event) {
    event.preventDefault();
      fetch('https://query2.finance.yahoo.com/v10/finance/quoteSummary/tsla?modules=price').then(response => {
          return response.json();
      }).then((data) => {
        price = data;
        setPrice(price);
      });
  }
  return (
    <div>
      <form>
        <input className = "inputValue" type="text" placeholder="Enter city name" onChange = {onChangeHandler}></input>
        <button onClick = {onClickHandler} >
          Submit!
        </button>
        <h1>Celsius:{prices}</h1>
      </form>
    </div>
  );
}

export default UserInput;
