import { useState } from "react/cjs/react.development";
import {Bar} from 'react-chartjs-2';
function UserInput() {
  var name, mainPrices = [];
  var pricing = [];
  const [prices, setPrice] = useState();
  function onChangeHandler(event) {
    name = event.target.value;
  }
  function onClickHandler(event) {
    event.preventDefault();
      fetch('https://www.alphavantage.co/query?function=TIME_SERIES_MONTHLY_ADJUSTED&symbol='+name+'&apikey=HIOOYZ15LELZLW0A').then(response => {
          return response.json();
      }).then((data) => {
       pricing = data['Monthly Adjusted Time Series'];
       console.log(pricing);
       var year = 2017;
       var OK1 = 0, OK2 = 0, OK3 = 0, OK4 = 0;
       for(const [key, value] of Object.entries(pricing)) {
         if((key == ''+year+'-04-30' || key == ''+year+'-06-30') && OK1 == 0) {
           console.log(key);
           mainPrices.push(key.toString());
           OK1 = 1;
         }
         if((key == ''+ (year + 1) +'-04-30' || key == ''+ (year + 1) +'-06-30') && OK2 == 0 ) {
           console.log(key);
           mainPrices.push(key.toString());
           OK2 = 1;
         }
         if((key == ''+ (year + 2) +'-04-30' || key == ''+ (year + 2) +'-06-30') && OK3 == 0) {
          console.log(key);
          mainPrices.push(key.toString());
          OK3 = 1;
        }
        if((key == ''+ (year + 3) +'-04-30' || key == ''+ (year + 3) +'-06-30') && OK4 == 0) {
          console.log(key);
          mainPrices.push(key.toString());
          OK4 = 1;
        }
       }
       console.log(Number(pricing[mainPrices[0]]['1. open']));
       console.log(Number(pricing[mainPrices[1]]['1. open']));
       console.log(Number(pricing[mainPrices[2]]['1. open']));
       console.log(Number(pricing[mainPrices[3]]['1. open']));
      });
      const state = {
        labels: ['January', 'February', 'March',
                 'April', 'May'],
        datasets: [
          {
            label: 'Rainfall',
            backgroundColor: 'rgba(75,192,192,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 2,
            data: [Number(pricing[mainPrices[0]]['1. open']), Number(pricing[mainPrices[1]]['1. open']), Number(pricing[mainPrices[2]]['1. open']), Number(pricing[mainPrices[3]]['1. open'])]
          }
        ]
      }
      return (
        <div>
          <Bar
            data={state}
            options={{
              title:{
                display:true,
                text:'Average Rainfall per month',
                fontSize:20
              },
              legend:{
                display:true,
                position:'right'
              }
            }}
          />
        </div>
      );
  }
  return (
    <div>
      <form>
        <input className = "inputValue" type="text" placeholder="Enter city name" onChange = {onChangeHandler}></input>
        <button onClick = {onClickHandler} >
          Submit!
        </button>
        
      </form>
    </div>
  );
}

export default UserInput;
