function UserInput() {
  var name;
  function onChangeHandler(event) {
    name = event.target.value;
  }
  function onClickHandler() {
      fetch('http://api.openweathermap.org/data/2.5/weather?q='+name+'&appid=52b987646ad8d9b43f889187f3d68809')
      .then((response) => {
          return response.json();
      })
      .then((data) => {
          return data;
        })
      .catch(err => alert("Wrong city name!"));
  }
  return (
    <div>
      <form>
        <input className = "inputValue" type="text" placeholder="Enter city name" onChange = {onChangeHandler}></input>
        <button onClick = {onClickHandler} >
          Submit!
        </button>
      </form>
    </div>
  );
}

export default UserInput;
